       yaxt="n",
       xaxt="n",
       ylim=c(-0.4,0.1),
       xlim=c(14,28),
       cex.axis=1.2,cex.lab=1.4)

#update axis
axis(1,at=c(17,25), 
     labels=c("17","25"),cex.axis=1.2)
